package com.hundsun.lcpt.algorithm;

public class Main {
	public static void  main(String args[]){
		//加密方法，"111" 为需要加密的字符串，"AHsAndMo"为密钥，密钥字符串长度至少为8位
		String ss = DES.enc("111欧文", "AHsAndMo");
		System.out.println(ss);
		//解密方法，ss为需要解密的字符串，"AHsAndMo"为密钥，密钥字符串长度至少为8位
		System.out.println(DES.dec(ss, "AHsAndMo"));
		
		//String s1 = "";
		 String s2 = "";
		System.out.println(DES.dec(s2, "AHsAndMo"));
	}
}